import React from "react";
import Header from "../components/Header";
import ArticleFeeder from "./ArticleFeeder";

export default class TopicArticles extends React.Component {
  state = {
    articles: []
  };
  render() {
    const { slug, desc } = this.props.location.state;
    return (
      <>
        <header>
          <Header user={this.props.user} />
        </header>
        <main>
          <section className="Topic">
            <p>{slug}</p>
            <p>{desc}</p>
            <ArticleFeeder
              slug={slug}
              filterArticlesByTopic={this.filterArticlesByTopic}
            />
          </section>
        </main>
      </>
    );
  }

  filterArticlesByTopic(slug, articles) {
    if (slug) {
      const topics = articles.filter(article => {
        console.log(article);
        return article.topic === slug;
      });

      console.log(topics);
    }
    console.log(articles);
    // console.log(this.state);

    // console.log(slug);
    // console.log(articles);
  }
}
