import React from "react";
import { Link } from "@reach/router";

const ArticleCard = props => {
  return (
    <>
      <div className="article_card_container">
        <Link to={`/articles/${props.id}`}>
          <li className="article_card">
            {/* <img
            src="https://cdn2.vectorstock.com/i/1000x1000/42/71/temporary-film-job-text-background-word-cloud-vector-16054271.jpg"
            alt="article"
            className="article_card_img"
          /> */}
            <p id="article_card_text">{props.date}</p>
            <p id="article_card_text">{props.topic}</p>
            <p id="article_card_text">{props.comments}</p>
            <p id="article_card_text"> {props.title}</p>
          </li>
        </Link>
      </div>
    </>
  );
};
export default ArticleCard;
