import React from "react";
import * as api from "../api";
import Header from "./Header";

export default class Article extends React.Component {
  state = {
    article: {},
    comments: []
  };

  render() {
    const { title, created_at, author, votes, body } = this.state.article;
    const { comments } = this.state;
    return (
      <>
        <header>
          {" "}
          <Header />
        </header>
        <main>
          <div>
            <h4>{title}</h4>
            <p>{created_at}</p>
            <p>{author}</p>
            <p>{votes}</p>
            <p>{body}</p>
          </div>
          <div>Comments</div>
        </main>
      </>
    );
  }

  componentDidMount() {
    this.fetchArticle(this.props.article_id);
    this.fetchComments(this.props.article_id);
  }

  fetchArticle(id) {
    api.getArticleById(id).then(article => {
      this.setState({ article: article });
    });
  }

  fetchComments(id) {
    api.getArticleComments(id).then(comments => {
      this.setState({ comments: comments });
      console.log(this.state);
    });
  }
}
