import React from "react";

const SortArticles = props => {
  console.log(props);
  return (
    <form>
      Sort Articles By <br />
      <label>
        DATE
        <input
          type="checkbox"
          name="check"
          value="Date"
          onClick={event => handleCheck(this.props.fetchArticles)}
        />
      </label>{" "}
      <br />
      <label>
        Comments
        <input
          type="checkbox"
          name="check"
          value="Comments"
          onClick={event => handleCheck(this.props.fetchArticles)}
        />
      </label>
      <br />
      <label>
        Votes
        <input
          type="checkbox"
          name="check"
          value="Votes"
          onClick={event => handleCheck(this.props.fetchArticles)}
        />
      </label>
    </form>
  );
};

const handleCheck = fetchArticles => {
  // const checkboxes = target.name;
  // checkboxes.forEach(box => {
  //   if (box !== target) box.checked = false;
  // });
  fetchArticles();
};

export default SortArticles;
