import React from "react";

const SortArticles = props => {
  return (
    <form>
      Sort Articles By <br />
      <label>
        DATE
        <input
          type="checkbox"
          name="check"
          value="created_at"
          onClick={event =>
            handleCheck(event.target.value, props.fetchArticles)
          }
        />
      </label>{" "}
      <br />
      <label>
        Comments
        <input
          type="checkbox"
          name="check"
          value="comment_count"
          onClick={event =>
            handleCheck(event.target.value, props.fetchArticles)
          }
        />
      </label>
      <br />
      <label>
        Votes
        <input
          type="checkbox"
          name="check"
          value="votes"
          onClick={event =>
            handleCheck(event.target.value, props.fetchArticles)
          }
        />
      </label>
    </form>
  );
};

const handleCheck = (sort_term, fetchArticles) => {
  // const checkboxes = target.name;
  // checkboxes.forEach(box => {
  //   if (box !== target) box.checked = false;
  // });
  fetchArticles();
};

export default SortArticles;
