import React from "react";
import * as api from "../api";
import Header from "./Header";

import CommentCard from "./subComponents/CommentCard";
import VotesIncrementer from "./subComponents/VotesIncrementer";
import ErrorShower from "../components/ErrorShower";

export default class Article extends React.Component {
  state = {
    article: {},
    comments: [],
    isLoading: true,
    err: null,
    voteChange: 0
  };

  render() {
    if (this.state.isLoading) return <p>Loading...</p>;
    if (this.state.err !== null) return <ErrorShower err={this.state.err} />;
    const { title, created_at, author, votes, body } = this.state.article;
    const { comments } = this.state;
    return (
      <>
        <header>
          {" "}
          <Header />
        </header>
        <main>
          <div>
            <div>
              <h4>{title}</h4>
              <VotesIncrementer
                articleVotes={this.state.article.votes}
                id={this.state.article.article_id}
                name="articleVoter"
              />
            </div>

            <p>{created_at}</p>
            <p>{author}</p>
            <p>{votes + this.state.voteChange}</p>
            <p>{body}</p>
          </div>
          <div>
            Comments
            <ul>
              {comments.map(comment => {
                return (
                  <div key={comment.comment_id}>
                    <CommentCard
                      key={comment.comment_id}
                      id={comment.comment_id}
                      author={comment.author}
                      body={comment.body}
                      date={comment.created_at}
                      votes={comment.votes}
                    />
                    <VotesIncrementer
                      commentVotes={comment.votes}
                      id="commentVoter"
                    />
                  </div>
                );
              })}
            </ul>
          </div>
        </main>
      </>
    );
  }

  componentDidMount() {
    this.fetchArticle(this.props.article_id);
    this.fetchComments(this.props.article_id);
  }

  fetchArticle(id) {
    api
      .getArticleById(id)
      .then(article => {
        this.setState({ article: article, isLoading: false });
      })
      .catch(err => {
        console.log(err, "<<<<<<<<<<ERR");
        this.setState({
          err: { status: err.response.status, msg: err.response.data.msg },
          isLoading: false
        });
      });
  }

  fetchComments(id) {
    api
      .getArticleComments(id)
      .then(comments => {
        this.setState({ comments: comments, isLoading: false });
      })
      .catch(err => {
        console.log(err, "<<<<<<<<<<ERR");
        this.setState({
          err: { status: err.response.status, msg: err.response.data.msg },
          isLoading: false
        });
      });
  }

  handleVoteChange = amnt => {
    this.setState({ voteChange: amnt });
  };
}
