import React from "react";

export default class SortArticles extends React.Component {
  // state = {
  //   created_at: false,
  //   comment_count: false,
  //   votes: false
  // };

  render() {
    return (
      <form>
        Sort Articles By <br />
        <label>
          DATE
          <input
            type="radio"
            name="check"
            value="created_at"
            checked
            onChange={event =>
              this.handleCheck(event.target, this.props.fetchArticles)
            }
          />
        </label>{" "}
        <br />
        <label>
          Comments
          <input
            type="radio"
            name="check"
            value="comment_count"
            checked={false}
            onChange={event =>
              this.handleCheck(event.target, this.props.fetchArticles)
            }
          />
        </label>
        <br />
        <label>
          Votes
          <input
            type="radio"
            name="check"
            value="votes"
            checked={false}
            onChange={event =>
              this.handleCheck(event.target, this.props.fetchArticles)
            }
          />
        </label>
      </form>
    );
  }

  handleCheck = (target, fetchArticles) => {
    target.checked = true;
    // const checkboxes = target.name;
    // checkboxes.forEach(box => {
    //   if (box !== target) box.checked = false;
    // });
    if (target.checked) {
      fetchArticles(target.value);
      // this.setState({ [target.checked]: true });
    }
  };
}
