import React from "react";
import Header from "../Header";

export default class CommentMaker extends React.Component {
  state = {
    comment: {}
  };
  render() {
    return (
      <>
        <header>
          <Header user={this.props.user} />
        </header>
        <main>
          <form>
            Post an Comment <br />
            <label>
              Comment Content
              <textarea rows="9" cols="50"></textarea>
            </label>
            <button onClick={event => this.createComment(event)}>Submit</button>
          </form>
        </main>
      </>
    );
  }

  createComment(event) {
    event.preventDefault();
    console.log(event.target);
  }
}
