import axios from "axios";

const baseURL = "https://nc-news-julia.herokuapp.com/api";

export const getArticles = sort_term => {
  const query = sort_term ? `?sort_by=${sort_term}` : ``;
  return axios
    .get(`${baseURL}/articles?${query}`, { params: { sort_by: sort_term } })
    .then(({ data }) => {
      return data.articles;
    });
};

export const getArticleById = id => {
  return axios
    .get(`${baseURL}/articles/${id}`)
    .then(({ data }) => {
      return data.article;
    })
    .catch(console.log);
};

export const getArticleComments = id => {
  return axios
    .get(`${baseURL}/articles/${id}/comments`)
    .then(({ data }) => {
      return data.comments;
    })
    .catch(console.log);
};

export const patchArticleVotes = id => {
  return axios
    .patch(`${baseURL}/articles/${id}`, { inc_votes: 1 })
    .then(({ data }) => {
      return data.article;
    })
    .catch(console.log);
};
