import React from "react";
import * as api from "../../api";

export default class VotesIncrementer extends React.Component {
  state = {
    hasVotedUp: false,
    txt: "VOTE üëç"
  };
  render() {
    return (
      <>
        <button onClick={event => this.incrementVotes(this.props)}>
          <span role="img" aria-label="thumbsUp">
            {this.state.txt}
          </span>
        </button>
        <p>VOTE</p>
      </>
    );
  }

  incrementVotes(prop) {
    if (this.state.hasVotedUp === false) {
      prop.displayVote(1);
      api.patchArticleVotes(prop.id, prop.name, 1).then(article => {
        // need to set the state in Article.jsx to the returned article and display the result
        this.setState({ hasVotedUp: true, txt: "UNVOTE üëé" });
      });
    } else {
      prop.displayVote(0);
      api.patchArticleVotes(prop.id, prop.name - 1).then(article => {
        this.setState({ hasVotedUp: false, txt: "VOTE üëç" });
      });
    }
  }
}
