import React from "react";
import * as api from "../api";
import Header from "./Header";

import CommentCard from "./subComponents/CommentCard";
import VotesIncrementer from "./subComponents/VotesIncrementer";

export default class Article extends React.Component {
  state = {
    article: {},
    comments: []
  };

  render() {
    const { title, created_at, author, votes, body } = this.state.article;
    const { comments } = this.state;
    return (
      <>
        <header>
          {" "}
          <Header />
        </header>
        <main>
          <div>
            <div>
              <h4>{title}</h4>
              <VotesIncrementer />
            </div>

            <p>{created_at}</p>
            <p>{author}</p>
            <p>{votes}</p>
            <p>{body}</p>
          </div>
        </main>
      </>
    );
  }

  componentDidMount() {
    this.fetchArticle(this.props.article_id);
    this.fetchComments(this.props.article_id);
  }

  fetchArticle(id) {
    api.getArticleById(id).then(article => {
      this.setState({ article: article });
    });
  }

  fetchComments(id) {
    api.getArticleComments(id).then(comments => {
      this.setState({ comments: comments });
    });
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevState !== this.state) {
      return (
        <div>
          Comments
          <ul>
            {this.state.comments.map(comment => {
              return (
                <>
                  <CommentCard
                    key={comment.comment_id}
                    id={comment.comment_id}
                    author={comment.author}
                    body={comment.body}
                    date={comment.created_at}
                    votes={comment.votes}
                  />
                  <VotesIncrementer
                    articleVotes={this.state.article.votes}
                    commentVotes={comment.votes}
                  />
                  ;
                </>
              );
            })}
          </ul>
        </div>
      );
    }
  }
}
