import React from "react";

export default class SortArticles extends React.Component {
  render() {
    return (
      <form>
        Sort Articles By <br />
        <label>
          DATE
          <input
            type="checkbox"
            name="check"
            value="created_at"
            onClick={event =>
              this.handleCheck(event.target.value, this.props.fetchArticles)
            }
          />
        </label>{" "}
        <br />
        <label>
          Comments
          <input
            type="checkbox"
            name="check"
            value="comment_count"
            onClick={event =>
              this.handleCheck(event.target.value, this.props.fetchArticles)
            }
          />
        </label>
        <br />
        <label>
          Votes
          <input
            type="checkbox"
            name="check"
            value="votes"
            onClick={event =>
              this.handleCheck(event.target.value, this.props.fetchArticles)
            }
          />
        </label>
      </form>
    );
  }

  handleCheck = (sort_term, fetchArticles) => {
    // const checkboxes = target.name;
    // checkboxes.forEach(box => {
    //   if (box !== target) box.checked = false;
    // });
    fetchArticles(sort_term);
  };
}
