import axios from "axios";

export const getArticles = () => {
  const baseURL = "https://nc-news-julia.herokuapp.com/api";
  return axios
    .get(`${baseURL}/articles`)
    .then(data => {
      console.log(data);
      return data.articles;
    })
    .catch(console.log);
};
