import React from "react";
import { Link } from "@reach/router";

const ArticleCard = props => {
  return (
    <>
      <Link to={`/articles/${props.id}`}>
        <li className="article_card">
          <p>{props.date}</p>
          <p>{props.topic}</p>
          <p>{props.comments}</p>
          <p> {props.title}</p>
        </li>
      </Link>
    </>
  );
};
export default ArticleCard;
