import React from "react";
import * as api from "../api";

import ArticleCard from "../components/subComponents/ArticleCard";
import ArticleSorter from "../components/subComponents/ArticleSorter";

export default class ArticleFeeder extends React.Component {
  state = {
    articles: [],
    isLoading: true,
    err: null
  };
  render() {
    // const {
    //   user: { username }
    // } = this.props;
    // console.log(username);
    const { articles } = this.state;
    if (this.state.isLoading)
      return <p className="loadIndicator">Loading...</p>;
    if (this.state.err !== null)
      return (
        <>
          <h1>this.state.err.status</h1>
          <h2>this.state.err.msg</h2>
        </>
      );
    return (
      <section className="content">
        <div className="article_sorter">
          <ArticleSorter fetchArticles={this.fetchArticles} />
        </div>
        <ul className="all_articles">
          <h3>ARTICLES</h3>
          {articles.map(article => {
            return (
              <ArticleCard
                key={article.article_id}
                id={article.article_id}
                date={article.created_at}
                topic={article.topic}
                title={article.title}
                comments={article.comment_count}
              />
            );
          })}
        </ul>
      </section>
    );
  }

  componentDidMount() {
    this.fetchArticles();
  }

  fetchArticles = sort_term => {
    api
      .getArticles(sort_term)
      .then(articles => {
        this.setState({ articles: articles, isLoading: false });
      })
      .catch(err => {
        this.setState({
          err: { status: err.response.status, msg: err.response.data.msg },
          isLoading: false
        });
      });
    this.filterArticlesByTopic(this.props.slug);
  };

  filterArticlesByTopic(slug) {
    if (slug) {
