import React from "react";
import * as api from "../../api";

export default class VotesIncrementer extends React.Component {
  state = {
    hasVotedUp: false
  };
  render() {
    return (
      <>
        <button onClick={event => this.incrementVotes(this.props)}>
          <span role="img" aria-label="thumbsUp">
            VOTE UP üëç
          </span>
        </button>
        <p>VOTE</p>
      </>
    );
  }

  incrementVotes(prop) {
    if (hasVotedUp === false) {
      if (prop.name === "articleVoter") {
        api.patchArticleVotes(prop.id).then(article => {
          // need to set the state in Article.jsx to the returned article and display the result
          this.setState({ hasVotedUp: true });
        });
      }
    }
  }
}
