import React from "react";
import * as api from "../api";

import ArticleCard from "../components/subComponents/ArticleCard";
import SortArticles from "../components/subComponents/SortArticles";

export default class ArticleFeed extends React.Component {
  state = {
    articles: []
  };

  render() {
    const { articles } = this.state;
    return (
      <div>
        <div>
          {/* SORT */}
          <SortArticles fetchArticles={this.fetchArticles} />
        </div>
        <ul>
          <h3>ARTICLES</h3>
          {articles.map(article => {
            return (
              <ArticleCard
                key={article.article_id}
                date={article.created_at}
                topic={article.topic}
                title={article.title}
              />
            );
          })}
        </ul>
      </div>
    );
  }

  componentDidMount() {
    this.fetchArticles();
  }

  fetchArticles = () => {
    api.getArticles().then(articles => {
      this.setState({ articles: articles });
    });
  };
}
