import React from "react";

export default class SortArticles extends React.Component {
  state = {
    created_at: false,
    comment_count: false,
    votes: false
  };

  render() {
    return (
      <form>
        Sort Articles By <br />
        <label>
          DATE
          <input
            type="checkbox"
            name="check"
            value="created_at"
            checked={this.state.created_at}
            onChange={event =>
              this.handleCheck(event.target, this.props.fetchArticles)
            }
          />
        </label>{" "}
        <br />
        <label>
          Comments
          <input
            type="checkbox"
            name="check"
            value="comment_count"
            checked={this.state.comment_count}
            onChange={event =>
              this.handleCheck(event.target, this.props.fetchArticles)
            }
          />
        </label>
        <br />
        <label>
          Votes
          <input
            type="checkbox"
            name="check"
            value="votes"
            checked={this.state.votes}
            onChange={event =>
              this.handleCheck(event.target, this.props.fetchArticles)
            }
          />
        </label>
      </form>
    );
  }

  handleCheck = (target, fetchArticles) => {
    // const checkboxes = target.name;
    // checkboxes.forEach(box => {
    //   if (box !== target) box.checked = false;
    // });
    if (!this.state[target.checked]) {
      fetchArticles(target.value);
      this.setState({ [target.checked]: true });
    }
  };
}
