import React from "react";
import * as api from "../api";
import Header from "./Header";

import CommentCard from "./subComponents/CommentCard";

export default class Article extends React.Component {
  state = {
    article: {},
    comments: []
  };

  render() {
    const { title, created_at, author, votes, body } = this.state.article;
    return (
      <>
        <header>
          {" "}
          <Header />
        </header>
        <main>
          <div>
            <h4>{title}</h4>
            <p>{created_at}</p>
            <p>{author}</p>
            <p>{votes}</p>
            <p>{body}</p>
          </div>
          <div>
            Comments
            <ul>
              {this.state.comments.map(comment => {
                return (
                  <li>
                    <CommentCard
                      key={comment.comment_id}
                      id={comment.comment_id}
                      author={comment.author}
                      body={comment.body}
                      date={comment.created_at}
                      votes={comment.votes}
                    />
                  </li>
                );
              })}
            </ul>
          </div>
        </main>
      </>
    );
  }

  componentDidMount() {
    this.fetchArticle(this.props.article_id);
    this.fetchComments(this.props.article_id);
  }

  fetchArticle(id) {
    api.getArticleById(id).then(article => {
      this.setState({ article: article });
    });
  }

  fetchComments(id) {
    api.getArticleComments(id).then(comments => {
      this.setState({ comments: comments });
      console.log(this.state);
    });
  }
}
