import React from "react";

const SortArticles = props => {
  // state = {
  //   created_at: false,
  //   comment_count: false,
  //   votes: false
  // };

  return (
    <form>
      Sort Articles By <br />
      <label>
        DATE
        <input
          type="radio"
          name="check"
          checked
          value="created_at"
          onChange={event => handleCheck(event.target, props.fetchArticles)}
        />
      </label>{" "}
      <br />
      <label>
        Comments
        <input
          type="radio"
          name="check"
          value="comment_count"
          onChange={event => handleCheck(event.target, props.fetchArticles)}
        />
      </label>
      <br />
      <label>
        Votes
        <input
          type="radio"
          name="check"
          value="votes"
          onChange={event => handleCheck(event.target, props.fetchArticles)}
        />
      </label>
    </form>
  );
};

const handleCheck = (target, fetchArticles) => {
  // const checkboxes = target.name;
  // checkboxes.forEach(box => {
  //   if (box !== target) box.checked = false;
  // });
  fetchArticles(target.value);
};

export default SortArticles;
